import {
  FormControl,
  FormControlLabel,
  FormLabel,
  Radio,
  RadioGroup,
  Stack,
  Typography,
} from "@mui/material";
import { PollInfo } from "./PollsPage";
import React from "react";
import axios from "axios";
import { POLL_SERVER_POLL_OPTIONS } from "../constants/Routes";
import { useAuth } from "../customHooks/useAuth";

interface ViewPollPageProps {
  poll: PollInfo;
}

interface PollOption {
  id: number;
  text: string;
}

const ViewPollPage = (props: ViewPollPageProps): JSX.Element => {
  const [pollOptions, setPollOptions] = React.useState<PollOption[]>([]);
  const [isLoading, setIsLoading] = React.useState<boolean>(false);
  const [hasError, setHasError] = React.useState<boolean>(false);
  const [selectedOption, setSelectedOption] = React.useState<number>();
  const [errorMessage, setErrorMessage] = React.useState<string>("");

  const { authToken } = useAuth();

  const fetchPollOptions = async (pollId: number) => {
    setIsLoading(true);
    setHasError(false);
    setErrorMessage("");
    try {
      const res = await axios.get(POLL_SERVER_POLL_OPTIONS, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
      console.log("poll options", res.data.pollOptions);
      let pollOptionsData: PollOption[] = [];
      pollOptionsData = res.data.pollOptions.map((pollOption: any) => {
        return {
          id: pollOption.id,
          text: pollOption.text,
        };
      });
    } catch (error) {
      console.log("Error in fetching polls", error);
      setHasError(true);
      setErrorMessage((error as Error).message);
    } finally {
      setIsLoading(false);
    }
  };

  React.useEffect(() => {
    fetchPollOptions(props.poll.id);
  }, []);

  return (
    <>
      {isLoading ? (
        <Typography variant="h2">Fetching poll information</Typography>
      ) : hasError ? (
        <Typography variant="h2">Something went wrong from our end</Typography>
      ) : (
        <>
          <Stack>
            <Typography variant="h2">{props.poll.title}</Typography>
            <Typography variant="h4">{props.poll.description}</Typography>
            {pollOptions.length === 0 ? (
              <Typography variant="body1">
                No poll options to show for the poll
              </Typography>
            ) : (
              <>
                <FormControl>
                  <FormLabel id="demo-controlled-radio-buttons-group">
                    Poll options:
                  </FormLabel>
                  <RadioGroup
                    aria-labelledby="demo-controlled-radio-buttons-group"
                    name="controlled-radio-buttons-group"
                    value={selectedOption}
                    onChange={(value) => setSelectedOption(Number(value))}
                  >
                    {pollOptions.map((pollOption) => (
                      <FormControlLabel
                        key={pollOption.id}
                        value={pollOption.id}
                        control={<Radio />}
                        label={pollOption.text}
                      />
                    ))}
                  </RadioGroup>
                </FormControl>
              </>
            )}
          </Stack>
        </>
      )}
    </>
  );
};

export default ViewPollPage;
