import {
  Button,
  Card,
  CardActions,
  CardContent,
  Typography,
} from "@mui/material";
import { PollInfo } from "../pages/PollsPage";
import { redirect } from "react-router-dom";

interface PollCardProps {
  pollInfo: PollInfo;
  buttonText: string;
  onClickHandler: (poll: PollInfo) => void;
}

const PollCard = (props: PollCardProps): JSX.Element => {
  return (
    <Card
      variant="outlined"
      sx={{
        width: "400px",
        maxHeight: "600px",
        display: "flex",
        justifyContent: "space-between",
        flexDirection: "column",
      }}
    >
      <CardContent>
        <Typography variant="h4">{props.pollInfo.title}</Typography>
        <Typography variant="subtitle1">
          {props.pollInfo.description}
        </Typography>
      </CardContent>
      <CardActions>
        <Button
          disableElevation
          onClick={() => {
            props.onClickHandler(props.pollInfo);
            redirect("/poll-result");
          }}
        >
          {props.buttonText}
        </Button>
      </CardActions>
    </Card>
  );
};

export default PollCard;
