import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import "./App.css";
import { Box, createTheme } from "@mui/material";
import { ThemeProvider } from "@mui/material";
import PollsPage, { PollInfo } from "./pages/PollsPage";
import CreatePollPage from "./pages/CreatePollPage";
import { AuthenticatorProvider, useAuth } from "./customHooks/useAuth";
import { PollPageType } from "./types/Poll";
import axios from "axios";
import { POLL_SERVER_GET_ALL_POLLS } from "./constants/Routes";

const theme = createTheme({
  typography: {
    fontFamily: ["Poppins", "sans-serif"].join(","),
    button: {
      textTransform: "none",
    },
  },
});

const pollsInfo: PollInfo[] = [
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
];

const dummyHandler = () => {};

function App() {
  const [polls, setPolls] = React.useState<PollInfo[]>([]);
  const [isLoading, setIsLoading] = React.useState<boolean>(false);
  const [hasError, setHasError] = React.useState<boolean>(false);
  const [errorMessage, setErrorMessage] = React.useState<string>("");

  const { authToken } = useAuth();

  const fetchPolls = async () => {
    setIsLoading(true);
    setHasError(false);
    setErrorMessage("");

    try {
      const res = await axios.get(POLL_SERVER_GET_ALL_POLLS, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
      console.log("Fetch polls", res);
    } catch (error) {
      setHasError(true);
      setErrorMessage((error as Error).message);
    } finally {
      setIsLoading(false);
    }
  };

  React.useEffect(() => {
    fetchPolls();
  }, []);

  return (
    <Router>
      <ThemeProvider theme={theme}>
        <AuthenticatorProvider>
          <>
            <Navbar />
            <Box marginLeft="5%" marginRight="5%">
              <Routes>
                <Route
                  path="/"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.ONGOING}
                      polls={pollsInfo}
                      pollCardButtonText="Vote"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
                <Route
                  path="/past-polls"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.PAST}
                      polls={pollsInfo}
                      pollCardButtonText="Show vote results"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
                <Route path="/create-poll" element={<CreatePollPage />} />
                <Route
                  path="/manage-poll"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.MANAGE}
                      polls={pollsInfo}
                      pollCardButtonText="View poll"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
              </Routes>
            </Box>
          </>
        </AuthenticatorProvider>
      </ThemeProvider>
    </Router>
  );
}

export default App;
