import React from "react";
import { Box, Stack, Typography } from "@mui/material";
import PollCard from "../components/PollCard";
import { PollPageType } from "../types/Poll";
import axios from "axios";
import { POLL_SERVER_GET_ALL_POLLS } from "../constants/Routes";
import { useAuth } from "../customHooks/useAuth";

export interface PollInfo {
  id: number;
  title: string;
  description: string;
}

interface ActivePollsPageProps {
  pollPageType: PollPageType;
  polls: PollInfo[];
  pollCardButtonText: string;
  pollCardHandler: () => void;
}

const PollsPage = (props: ActivePollsPageProps): JSX.Element => {
  const [polls, setPolls] = React.useState<PollInfo[]>([]);
  const [isLoading, setIsLoading] = React.useState<boolean>(false);
  const [hasError, setHasError] = React.useState<boolean>(false);
  const [errorMessage, setErrorMessage] = React.useState<string>("");

  const { authToken } = useAuth();

  const fetchPolls = async () => {
    setIsLoading(true);
    setHasError(false);
    setErrorMessage("");
    try {
      const res = await axios.get(POLL_SERVER_GET_ALL_POLLS, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
      let pollsData: PollInfo[] = [];
      console.log("polls data", res.data.polls);
      pollsData = res.data.polls.map((poll: any) => {
        return {
          id: poll.id,
          title: poll.title,
          desciption: poll.description,
        };
      });
      setPolls(pollsData);
    } catch (error) {
      console.log("Error in fetching polls", error);
      setHasError(true);
      setErrorMessage((error as Error).message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Box marginTop="2%">
      <Typography variant="h2">Ongoing polls</Typography>
      <Stack
        direction="row"
        marginTop="4em"
        columnGap="4em"
        rowGap="4em"
        display="flex"
        flexWrap="wrap"
      >
        {props.polls.map((poll) => (
          <PollCard
            key={poll.id}
            cardTitle={poll.title}
            cardDescription={poll.description}
            buttonText={props.pollCardButtonText}
            onClickHandler={props.pollCardHandler}
          />
        ))}
      </Stack>
    </Box>
  );
};

export default PollsPage;
