import React from "react";

const CreatePollPage = (): JSX.Element => {
  const [isLoading, setIsLoading] = React.useState<boolean>(false);
  const [hasError, setHasError] = React.useState<boolean>(false);
  const [selectedOption, setSelectedOption] = React.useState<number>();
  const [errorMessage, setErrorMessage] = React.useState<string>("");

  const createPoll = async () => {
    setIsLoading(true);
    setHasError(false);
    setErrorMessage("");
    try {
      const res = await axios.get(POLL_SERVER_POLL_OPTIONS, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
      console.log("poll options", res.data.pollOptions);
      let pollOptionsData: PollOption[] = [];
      pollOptionsData = res.data.pollOptions.map((pollOption: any) => {
        return {
          id: pollOption.id,
          text: pollOption.text,
        };
      });
    } catch (error) {
      console.log("Error in fetching polls", error);
      setHasError(true);
      setErrorMessage((error as Error).message);
    } finally {
      setIsLoading(false);
    }
  };
  return <>CreatePollPage</>;
};

export default CreatePollPage;
