import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import "./App.css";
import { Box, Container, createTheme } from "@mui/material";
import { ThemeProvider } from "@mui/material";
import PollsPage, { PollInfo } from "./pages/PollsPage";
import InactivePollsPage from "./pages/InactivePollsPage";
import ManagePollsPage from "./pages/ManagePollsPage";
import CreatePollPage from "./pages/CreatePollPage";
import { AuthenticatorProvider } from "./customHooks/useAuth";

const theme = createTheme({
  typography: {
    fontFamily: ["Poppins", "sans-serif"].join(","),
    button: {
      textTransform: "none",
    },
  },
});

const pollsInfo: PollInfo[] = [
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
  {
    title: "Poll Title",
    description: "Poll Description",
  },
];

const dummyHandler = () => {};

function App() {
  return (
    <Router>
      <ThemeProvider theme={theme}>
        <AuthenticatorProvider>
          <>
            <Navbar />
            <Box marginLeft="5%" marginRight="5%">
              <Routes>
                <Route
                  path="/"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.ONGOING}
                      polls={pollsInfo}
                      pollCardButtonText="Vote"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
                <Route
                  path="/past-polls"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.PAST}
                      polls={pollsInfo}
                      pollCardButtonText="Show vote results"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
                <Route path="/create-poll" element={<CreatePollPage />} />
                <Route
                  path="/manage-poll"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.MANAGE}
                      polls={pollsInfo}
                      pollCardButtonText="View poll"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
              </Routes>
            </Box>
          </>
        </AuthenticatorProvider>
      </ThemeProvider>
    </Router>
  );
}

export default App;
