import React from "react";
import { Routes, Route } from "react-router-dom";
import Navbar from "./components/Navbar";
import "./App.css";
import { Box, Typography, createTheme } from "@mui/material";
import { ThemeProvider } from "@mui/material";
import PollsPage, { PollInfo } from "./pages/PollsPage";
import CreatePollPage from "./pages/CreatePollPage";
import { useAuth } from "./customHooks/useAuth";
import { PollPageType } from "./types/Poll";
import axios from "axios";
import { POLL_SERVER_GET_ALL_POLLS } from "./constants/Routes";

const theme = createTheme({
  typography: {
    fontFamily: ["Poppins", "sans-serif"].join(","),
    button: {
      textTransform: "none",
    },
  },
});

const dummyHandler = () => {};

function App() {
  const [polls, setPolls] = React.useState<PollInfo[]>([]);
  const [isLoading, setIsLoading] = React.useState<boolean>(false);
  const [hasError, setHasError] = React.useState<boolean>(false);
  const [errorMessage, setErrorMessage] = React.useState<string>("");

  const { authToken, isAuthenticated } = useAuth();

  const fetchPolls = async () => {
    setIsLoading(true);
    setHasError(false);
    setErrorMessage("");

    try {
      const res = await axios.get(POLL_SERVER_GET_ALL_POLLS, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
      let pollsData: PollInfo[] = [];
      console.log("polls data", res.data.polls);
      pollsData = res.data.polls.map((poll: any) => {
        return {
          id: poll.id,
          title: poll.title,
          desciption: poll.description,
        };
      });
      setPolls(pollsData);
    } catch (error) {
      console.log("Error in fetching polls", error);
      setHasError(true);
      setErrorMessage((error as Error).message);
    } finally {
      setIsLoading(false);
    }
  };

  React.useEffect(() => {
    fetchPolls();
  }, [isAuthenticated]);

  return (
    <ThemeProvider theme={theme}>
      <>
        <Navbar />
        {isAuthenticated === true ? (
          <>
            <Box marginLeft="5%" marginRight="5%">
              <Routes>
                <Route
                  path="/"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.ONGOING}
                      polls={polls}
                      pollCardButtonText="Vote"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
                <Route
                  path="/past-polls"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.PAST}
                      polls={polls}
                      pollCardButtonText="Show vote results"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
                <Route path="/create-poll" element={<CreatePollPage />} />
                <Route
                  path="/manage-poll"
                  element={
                    <PollsPage
                      pollPageType={PollPageType.MANAGE}
                      polls={polls}
                      pollCardButtonText="View poll"
                      pollCardHandler={dummyHandler}
                    />
                  }
                />
              </Routes>
            </Box>
          </>
        ) : (
          <Typography variant="h2">Please login or sign up</Typography>
        )}
      </>
    </ThemeProvider>
  );
}

export default App;
