import React from "react";
import Modal from "@mui/material/Modal";
import IconButton from "@mui/material/IconButton";
import CloseIcon from "@mui/icons-material/Close";
import {
  Stack,
  Typography,
  Box,
  createTheme,
  ThemeProvider,
  Avatar,
  TextField,
  Button,
} from "@mui/material";
import { useAuth } from "../customHooks/useAuth";

const theme = createTheme({
  typography: {
    fontFamily: ["Poppins", "sans-serif"].join(","),
  },
});

interface SignUpModalProps {
  isOpen: boolean;
  closeModal?: () => void;
}

type FormState = Record<string, FormField>;

interface FormField {}

const SignUpModal = (props: SignUpModalProps): JSX.Element => {
  const [formState, setFormState] = React.useState<FormState>({
    name: {},
    email: {},
    password: {},
  });

  const { login, isLoading } = useAuth();

  const onChangeHandler = (fieldKey: string, fieldValue: string) => {
    let formField = formState[fieldKey];
    let newFormFieldState = {
      ...formField,
      value: fieldValue,
    };
    setFormState((prev) => {
      return {
        ...prev,
        fieldKey: newFormFieldState,
      };
    });
  };

  return (
    <>
      <ThemeProvider theme={theme}>
        {props.isOpen && (
          <Modal open={props.isOpen}>
            <div className="modal">
              <Box>
                <IconButton
                  onClick={props.closeModal}
                  sx={{ display: "flex", marginLeft: "auto" }}
                >
                  <CloseIcon />
                </IconButton>
                <Stack
                  direction="row"
                  alignItems="center"
                  justifyContent="center"
                  columnGap={2}
                  margin="40px"
                >
                  <Avatar
                    src={require("../assets/pollapp.jpeg")}
                    alt="Pollx Logo"
                    variant="square"
                    sx={{ height: "60px", width: "60px" }}
                  />
                  <Typography variant="h4" className="typography">
                    Join VoteX Today
                  </Typography>
                </Stack>
                <Stack rowGap={3}>
                  <Stack rowGap={1}>
                    <Typography>Name</Typography>
                    <TextField
                      id="outlined-basic"
                      label="Enter you name"
                      variant="outlined"
                    />
                  </Stack>
                  <Stack rowGap={1}>
                    <Typography>Email</Typography>
                    <TextField
                      id="outlined-basic"
                      label="Enter you email"
                      variant="outlined"
                    />
                  </Stack>
                  <Stack rowGap={1}>
                    <Typography>Password</Typography>
                    <TextField
                      id="outlined-basic"
                      label="Enter you password"
                      variant="outlined"
                      type="password"
                    />
                  </Stack>
                </Stack>
                <Button>Sign in</Button>
              </Box>
            </div>
          </Modal>
        )}
      </ThemeProvider>
    </>
  );
};

export default SignUpModal;
